async (job, page, request) => {
    // Extract experience level from description
    const description = job.description.toLowerCase();

    if (description.includes('senior') || description.includes('sr.') || description.includes('10+')) {
        job.experienceLevel = 'Senior (5+ years)';
    } else if (description.includes('junior') || description.includes('entry level') || description.includes('0-2')) {
        job.experienceLevel = 'Entry Level (0-2 years)';
    } else if (description.includes('mid-level') || description.includes('mid level') || description.includes('3-5')) {
        job.experienceLevel = 'Mid-Level (3-5 years)';
    } else {
        job.experienceLevel = 'Not Specified';
    }

    // Extract remote work information
    job.isRemote = description.includes('remote') || description.includes('work from home') || description.includes('distributed');
    job.isHybrid = description.includes('hybrid') || description.includes('flexible');

    // Extract salary if available
    const salaryMatch = description.match(/\$([0-9,]+)\s*-?\s*\$?([0-9,]*)/i);
    job.salaryExtracted = salaryMatch ? salaryMatch[0] : null;

    // Count key technologies if mentioned
    const techStack = ['Python', 'JavaScript', 'Java', 'React', 'Node.js', 'AWS', 'Docker', 'Kubernetes', 'SQL', 'PostgreSQL'];
    job.technologiesFound = techStack.filter(tech => description.includes(tech));

    return job;
}
